package com.xiaoshi.chattingrobot.http;import java.io.UnsupportedEncodingException;import java.net.URLEncoder;import java.security.MessageDigest;import java.util.ArrayList;import java.util.Arrays;import java.util.HashMap;import java.util.List;import android.util.TimeUtils;public class URLContainer {	public static final String APPKEY = "6e29c565";	public static final String SECRETKEY = "daffed346e29c5654f54133d1fc65ccb";	private static List<String> ignore = new ArrayList<String>();	/**	 * 获取服务器地址链接 根据你所需要的 传入相应的功能就能返回相应的Url	 * 	 * @param action	 * @return url	 */	public static String postUrl(String url) {		return url;	}	/***	 * 拼接Url	 * 	 * @param url	 * @param map	 * @return	 */	public static String getUrl(String url, HashMap<String, String> map) {		if (null == map || map.isEmpty()) {			return "";		}		StringBuffer sb = new StringBuffer();		sb.append(url).append("&");		for (String key : map.keySet()) {			sb.append(key + "=" + map.get(key) + "&");		}		return sb.substring(0, sb.length() - 1);	}}